truncate table command;


INSERT INTO `command` (`uid`, `command`, `template_args`, `reserved`) VALUES
('addMountPoint', '#/bin/bash\n\nset -e\n\nvg=${__vgName}\nlv=${__lvName}\nsize=${__lvSize}\nfolder=${__mountFolder}\nfs=${__filesystem}\n\n# Use /proc to find all processes that have opened file descriptors in the given folder.\nfolder_stop_all_processes() {\n    if [ -z \"$1\" ]; then\n        echo \"Usage: $0 <folder>\"\n        exit 0\n    fi\n    folder=\"$1\"\n                    \n    echo \"$0: stop all\"\n    # Try to stop the processes with a SIGTERM first.\n    cd /proc\n    for PID in `ls -d [0-9]*`; do \n        if ls -l ${PID}/fd 2>/dev/null | grep -q \"$folder\"; then\n            if ! ps -up $PID | grep -Eq \"sshd|bash|sudo\"; then \n                kill $PID || true\n            fi \n        fi\n    done\n    \n    sleep 1\n    \n    # If SIGTERM is not enough, seng SIGKILL.\n    echo \"$0: kill all\"\n    cd /proc\n    for PID in `ls -d [0-9]*`; do \n        if ls -l ${PID}/fd 2>/dev/null | grep -q \"$folder\"; then\n            if ! ps -up $PID | grep -Eq \"sshd|bash|sudo\"; then \n                kill -9 $PID || true\n            fi \n        fi\n    done\n}\n\nclone_folder() {\n    if [ -z \"$1\" ] || [ -z \"$2\" ]; then\n        echo \"Usage: $0 <folder>\"\n        exit 0\n    fi\n    folder=\"$1\"\n    dstFolder=\"$2\"\n    \n    cd $folder || exit 111\n    echo \"$0: tar --selinux --xattrs --acls --xattrs-include=* -p -c -f - . | (cd $dstFolder && tar --selinux --xattrs --acls xpf -)\"\n    tar --selinux --xattrs --acls --xattrs-include=* -p -c -f - . | (cd $dstFolder && tar --selinux --xattrs --acls --xattrs-include=* -x -p -f -)\n}\n\ntmpMount=\"\" # Global variable to get the value from tmp_mount() function.\ntmp_mount() {\n    if [ -z \"$1\" ]; then\n        echo \"Usage: $0 <mount_device>\"\n        exit 0\n    fi\n    mountDev=\"$1\"\n    mountDir=`mktemp -d`\n    echo \"mount $mountDev $mountDir\"\n    mount $mountDev $mountDir || exit 121\n    tmpMount=$mountDir\n}\n\nif [ $size -le 0 ]; then\n    # Lv has zero size: nothing to do.\n    exit 0\nfi\nif ! vgs -o vg_name | grep -q \" ${vg}\"; then\n    echo \"Volume group not found.\"\n    exit 11\nfi\nif ! echo $fs | grep -Eq "ext[2-4]|xfs|btrfs"; then\n    echo "Filesystems supported: ext[2-4]|xfs|btrfs"\n    exit 12\nfi\nif lvs -o lv_name | grep -q \" $lv \"; then\n    echo \"lv $lv already existent\"\nelse\n    echo \"lvcreate -n $lv -L ${size}M $vg\"\n    if ! lvcreate -n $lv -L ${size}M $vg; then\n        echo \"can\'t create lv $lv\"\n        exit 13\n    fi\nfi\n\nif lsblk -n -o FSTYPE /dev/${vg}/${lv} | grep -Eq \'ext[2-4]|xfs|btrfs\'; then\n    echo \"lv $lv already formatted\"\nelse\n    echo \"mkfs.${fs} /dev/${vg}/${lv}\"\n    if ! mkfs.${fs} /dev/${vg}/${lv}; then\n        echo \"can\'t format /dev/${vg}/${lv}\"\n        exit 15\n    fi\nfi\n\nif mount  | grep \"$folder\" | grep -E `lvs --noheadings -o lv_dm_path,lv_path /dev/${vg}/${lv} | sed -r -e \'s/^ +//g\' | tr \' \' \'|\'`; then\n    echo \"lv $lv already mounted on $folder\"\n    # TODO: check lv size here.\nelse\n    # The folder is already present (but not mounted on $lv), stop processes on it amd move the data.\n    if [ -d \"$folder\" ]; then\n        folder_stop_all_processes $folder\n        tmp_mount \"/dev/${vg}/${lv}\"\n        clone_folder $folder $tmpMount\n        cd $folder && rm -fr * || true # cleanup the data in the old place.\n        cd\n        echo \"umount -l \"/dev/${vg}/${lv}\" && rm -fr $tmpMount\"\n        umount -l \"/dev/${vg}/${lv}\" && rm -fr $tmpMount\n    else\n        mkdir -p $folder || exit 17\n    fi\n    mount /dev/${vg}/${lv} $folder\nfi\n          \nif ! grep -q \"/dev/${vg}/${lv}\" /etc/fstab; then\n    echo \"echo -e \\\"/dev/${vg}/${lv}\\t${folder}\\t$fs\\tdefaults\\t0\\t2\\\" >> /etc/fstab\"    \n    echo -e \"/dev/${vg}/${lv}\\t${folder}\\t$fs\\tdefaults\\t0\\t2\" >> /etc/fstab\nfi\n\nmount | grep \"$folder\"\n', '{\n\"__vgName\": \"str\",\n\"__lvName\": \"str\",\n\"__lvSize\": \"int\",\n\"__mountFolder\": \"str\",\n\"__filesystem\": \"str\"\n}', 1),
('addPubKey', '#/bin/bash\n\nset -e\n\npubKey=\"${__pubKey}\"\n\nif ! grep -q \"${pubKey}\" /root/.ssh/authorized_keys; then echo \"${pubKey}\" >> /root/.ssh/authorized_keys; fi', '{\n    \"__pubKey\": \"str\"\n}', 1),
('echo', '#/bin/bash\n\nset -e\n\necho \"${__echo}\"', '{\"__echo\": \"str\"}', 1),
('lvGrow', '#/bin/bash\n\nset -e\n\nvg=${__vgName}\nlv=${__lvName}\ntotSize=${__totSize}\ngrowSize=${__growSize}\ngrow_100=${__grow_100}\n\n# Order of precedence:\n# 1) if grow_100 is true, use lvextend -l +100%FRE device\n# 2) if $totSize is given, use lvextend -L${totSize}M device\n# 3) if $growSize is given, use lvextend -L+${growSize}M device\n\n# Check lv\nif [ ! -b /dev/${vg}/${lv} ]; then\n    echo /dev/${vg}/${lv}\n    exit 31    \nfi\n\nif [ \"$grow_100\" == \"True\" ]; then\n    echo \"lvextend -l +100%FREE /dev/${vg}/${lv}\"   \n    if ! lvextend -l +100%FREE /dev/${vg}/${lv}; then\n        echo \"Error on executing lvextend\" \n    fi\nelif [ $totSize -gt 0 ]; then\n    echo \"lvextend -L${totSize}M /dev/${vg}/${lv}\"\n    if ! lvextend -L${totSize}M /dev/${vg}/${lv}; then\n        echo \"Error on executing lvextend\"\n    fi\nelif [ -n \"$growSize\" ]; then\n    echo \"lvextend -L+${growSize}M /dev/${vg}/${lv}\"\n    if ! lvextend -L+${growSize}M /dev/${vg}/${lv}; then\n        echo \"Error on executing lvextend\"\n    fi\nfi\n\nif file -sL /dev/${vg}/${lv} | grep -Eq \'ext4|ext3|ext2\'; then\n    resize2fs /dev/${vg}/${lv}\nelif file -sL /dev/${vg}/${lv} | grep -iq \'xfs\'; then\n    xfs_growfs /dev/${vg}/${lv}\nelif file -sL /dev/${vg}/${lv} | grep -iq \'btrfs\'; then\n    # A mount point it\'s needed to grow a btrs.\n    mPoint=`mount | grep /dev/${vg}/${lv} | cut -f3 -d \" \"`\n    if [ -z \"$mPoint\" ]; then\n        dmPath=`lvs --noheadings -o lv_dmpath /dev/${vg}/${lv} | tr -d \" \"`\n        mPoint=`mount | grep \"$dmPath\" | cut -f3 -d \" \"`\n    fi\n    if [ -n \"$mPoint\" ]; then\n        echo \"btrfs filesystem resize max $mPoint\"\n        btrfs filesystem resize max $mPoint\n    else\n        echo \"Cannot find mount point for /dev/${vg}/${lv}\"\n    fi\nelif file -sL /dev/${vg}/${lv} | grep -iq "swap"; then\n    swapoff /dev/${vg}/${lv}\n    mkswap -f /dev/${vg}/${lv}\n    swapon /dev/${vg}/${lv}\nelse\n    echo \"Can\'t identify the filesystem on the device /dev/${vg}/${lv}, do not grow the filesystem.\"\n    exit 121\nfi', '{\n    \"__vgName\": \"str\",\n    \"__lvName\": \"str\",\n    \"__totSize\": \"int\",\n    \"__growSize\": \"int\",\n    \"__grow_100\": \"bool\"\n}', 1),
('reboot', '#/bin/bash\n\nreboot', '{}', 1),
('waitPowerOn', 'dummy', '{}', 1),
('removeBootstrapKey', '#/bin/bash\n\nset -e\n\npubKey=\"${__pubKey}\"\n\nsed -i -e \"\\|${pubKey}|d\" /root/.ssh/authorized_keys\n', '{}', 1),
('renameVg', '#/bin/bash\n\nset -e\n\nvg=${__vgName}\n        \n# Get default vg name.\ndefVg=$(vgs --noheadings -o vg_name | tr -d \" \")\necho \"defVg: $defVg\"\necho \"vg: $vg\"\n# Get all lv names.\nlVs=$(lvs --noheadings -o lv_name | tr -d \" \")\n# Get default lv mapper path of the root lv.\ndefRootLvPath=$(grep \" / \" /etc/fstab | cut -f1 -d\" \")\necho \"defRootLvPath: $defRootLvPath\"\n# Pick the lv name of the root lv.\nlvRoot=$(for lv in $lVs;do \n    if grep \" / \" /etc/fstab | grep -q $lv; then \n        echo $lv\n    fi\ndone)\n\n# Swap devices in fstab. Hash: lv[lvname] = /dev/mapper/...\ndeclare -A swapDevs\ndefSwapLvsPath=$(grep \" swap \" /etc/fstab | cut -d\" \" -f1)\n# Map lv path to lv names.\nfor lv in $lVs;do\n    lvPath=$(grep \" swap \" /etc/fstab | grep \"\\-${lv}\" | cut -f1 -d \" \")\n    if [ -n \"$lvPath\" ]; then\n        swapDevs[${lv}]=$lvPath\n    fi\ndone\n# Stop swaps.\nswapoff -a\n\n# Other devices in fstab. Hash: lv[lvname] = /dev/mapper/...\ndeclare -A otherDevs\ndefOtherLvsPath=$(grep -v \' / | swap\' /etc/fstab | grep -E \'ext4|ext3|xfs|brtfs\' | grep -E \"/dev/mapper|/dev/${defVg}\" | cut -d\" \" -f1)\n# Map lv path to lv names, exclude root and swaps.         \nfor lv in $lVs;do\n    lvPath=$(grep -Ev \' / | swap\' /etc/fstab | grep -E \'ext4|ext3|xfs|brtfs\' | grep \"\\-${lv}\" | cut -f1 -d \" \")\n    if [ -n \"$lvPath\" ]; then\n        otherDevs[${lv}]=$lvPath\n    fi\ndone\n         \n# Rename volume group\nif ! vgrename $defVg $vg; then \n    exit 13\nfi\n# Reactivate vg and lvs.\nvgchange -ay || exit 15\nfor lv in $lVs; do \n    if ! lvchange /dev/${vg}/$lv --refresh; then \n        exit 17\n    fi\ndone\n\n# Vg renamed: can get the new lv mapper path of the root lv.\nrootMap=$(lvs --noheadings -o lv_path -S lv_name=$lvRoot | tr -d \" \")\necho \"rootMap: $rootMap\"\n\n# Adjust fstab.\nsed -i -e \"s#${defRootLvPath}#${rootMap}#g\" /etc/fstab # If mapper path was used.\nif [ -n $swapDevs ]; then\n    for lv in \"${!swapDevs[@]}\"; do\n        sed -i -e \"s#${swapDevs[$lv]}#/dev/${vg}/${lv}#g\" /etc/fstab\n    done\nfi\nif [ -n $otherDevs ]; then\n    for lv in \"${!otherDevs[@]}\"; do\n        sed -i -e \"s#${otherDevs[$lv]}#/dev/${vg}/${lv}#g\" /etc/fstab\n    done\nfi\nsed -i -e \"s#/${defVg}#/${vg}#g\" /etc/fstab # If /dev/vgname/lvname was used.\n\n# Adjust grub.cfg, grubenv, default-grub. \nfor grubFile in /boot/grub/grub.cfg /boot/grub2/grub.cfg /boot/grub/grubenv /boot/grub2/grubenv /etc/default/grub /etc/sysconfig/grub; do \n    if [ -w $grubFile ]; then\n        sed -i -e \"s#${defRootLvPath}#${rootMap}#g\" $grubFile\n        sed -i -e \"s#/${defVg}#/${vg}#g\" -e \"s#${defVg}/#${vg}/#g\" $grubFile\n    fi\ndone\nif [ -d /boot/loader/entries ]; then\n    loaderFiles=$(ls /boot/loader/entries/*)\n    for lFile in $loaderFiles; do\n        sed -i -e \"s#${defRootLvPath}#${rootMap}#g\" $lFile\n        sed -i -e \"s#/${defVg}#/${vg}#g\" -e \"s#${defVg}/#${vg}/#g\" $lFile\n    done\nfi\n    \n# initramfs (adjust swap).\nif [ -w /etc/initramfs-tools/conf.d/resume ]; then\n    for lv in \"${!swapDevs[@]}\"; do\n        sed -i -e \"s#${swapDevs[$lv]}#/dev/${vg}/${lv}#g\" /etc/initramfs-tools/conf.d/resume\n    done\n    sed -i -e \"s#/${defVg}#/${vg}#\" /etc/initramfs-tools/conf.d/resume\nfi\nif [ -x /sbin/update-initramfs ]; then\n    echo \"Rebuilding initrd.\"\n    if ! update-initramfs -u; then \n        exit 19\n    fi\nfi\nif [ -x /sbin/dracut ]; then\n    echo \"Rebuilding initrd.\"\n    dracut --force\n    sleep 1\n    rm -rf /var/tmp/dracut*\n    sleep 0.5\nfi\n\nexit 0', '{\n    \"__vgName\": \"str\"\n}', 1),
('resizeLastPartition', '#/bin/bash\n\nset -e\n\n# Get the number of the last partition and if it\'s an LVM physical volume resize it.\ndev=${__diskDevice}\n\npN=0\npNums=$(grep \" ${dev}[0-9]\" /proc/partitions | awk \'{print $4}\' | sed \"s/${dev}//g\")\nfor n in $pNums; do\n    if [ $n -gt $pN ]; then \n        pN=$n\n    fi\ndone\necho \"Last partition: /dev/${dev}${pN}\"\n    \n# Resize the last partition if it\'s a pv, otherwise skip.\nif pvs /dev/${dev}${pN} > /dev/null 2>&1; then\n    printf \"d\\n${pN}\\nn\\np\\n${pN}\\n\\n\\nt\\n${pN}\\n8e\\nw\\n\" | fdisk /dev/${dev} || true\n    sleep 0.5\n    partprobe /dev/${dev}\n    sleep 1\n    pvresize /dev/${dev}${pN}\nelse\n    echo \"Partition /dev/${dev}${pN} is not a physical volume. Skip it.\" \nfi', '{\n\"__diskDevice\": \"str\"\n}', 1);


set foreign_key_checks = 1;
